        if (!params) params = {};

        if (x === undefined) x = '+0';
        if (y === undefined) y = '+0';

        var bar = new Bar(charm, x, y, params);
        multi.bars.push(bar);
        bar.offset = multi.offset;
        multi.on('offset', function (o) {
            bar.offset = o;
