    this.charm.pop(true);

    return this;
};

Bar.prototype.percent = function (p, msg) {
    if (p === undefined) {
        return this.progress.percent;
    }
    else {
        p = Math.min(100, p);
        this.progress.percent = p;
        this.progress.ratio = [ p, 100 ];

        this.draw(
            this.width * p / 100,
            msg || (Math.floor(p) + ' %')
        );

        return this;
    }
