Bar.prototype.ratio = function (n, d, msg) {
    if (n === undefined && d === undefined) {
        return this.progress.ratio;
    }
    else {
        var f = n / d;
        this.progress.ratio = [ n, d ];
        this.progress.percent = f * 100;

        this.draw(
            this.width * f,
            msg || (n + ' / ' + d)
        );

        return this;
